package HackerRank;import java.util.Scanner;public class AppleAndOrangeHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        /*s & t are house distance*/        final int s = scanner.nextInt();        final int t = scanner.nextInt();        final int a = scanner.nextInt(); /*Apple tree position*/        final int b = scanner.nextInt(); /*Orange tree position*/        final int m = scanner.nextInt(); /*number of apples*/        final int n = scanner.nextInt(); /*Number of oranges*/        final int[] apples = new int[m];        final int[] oranges = new int[n];        for(int i = 0; i < m; i++){            apples[i] = scanner.nextInt();        }        for(int i = 0; i < n; i++){            oranges[i] = scanner.nextInt();        }        scanner.close();        final int[] result = numberOfFallenFruitsInHouse(s, t, a, b, apples, oranges);        System.out.println(result[0]+"\n"+result[1]);    }    public static int[] numberOfFallenFruitsInHouse(            int s, int t, int a, int b, int[] apples, int[] oranges){        final int[] result = new int[2];        for(int apple : apples){ /*Add each apple with apple tree distance*/            apple += a;            if(apple >= s && apple <= t){ /*Make sure into house limit*/                result[0]++; /*Apple count*/            }        }        for(int orange : oranges){ /*Add each orange with orange tree distance*/            orange += b;            if(orange >= s && orange <= t){ /*Make sure into house limit*/                result[1]++; /*Orange count*/            }        }        return result;    }}