package HackerRank;import java.util.Scanner;public class CompareTheTripletsHR {    public static void main(String[] args) {        final int sizeArray = 3;        Scanner scanner = new Scanner(System.in);        final int[] alicePoints = new int[3];        final int[] bobPoints = new int[3];        for(int i = 0; i < sizeArray; i++){            alicePoints[i] = scanner.nextInt();        }        for(int i = 0; i < sizeArray; i++){            bobPoints[i] = scanner.nextInt();        }        scanner.close();        final int[] result = compareTheTriplets(alicePoints, bobPoints, sizeArray);        System.out.println(result[0] +" "+result[1]);    }    public static int[] compareTheTriplets(int[] alicePoints, int[] bobPoints, int sizeArray){        int[] counts = new int[2];        for(int i = 0; i < sizeArray; i++){            if(alicePoints[i] > bobPoints[i]){ /*Alice is awarded 1 point.*/                counts[0]++;            }            if(alicePoints[i] < bobPoints[i]){ /*Bob is awarded 1 point.*/                counts[1]++;            }        }        return counts;    }}