package HackerRank;import java.math.BigInteger;import java.util.Scanner;public class ExtraLongFactorialHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int number = scanner.nextInt();        scanner.close();        System.out.println(bigFactorial(number));    }    public static BigInteger bigFactorial(int number){        BigInteger factorial = BigInteger.ONE;        for(int i = 2; i <= number; i++){            factorial = factorial.multiply(new BigInteger(String.valueOf(i)));        }        return factorial;    }}