package HackerRank;import java.util.Scanner;public class FindDigitsHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int sizeArray = scanner.nextInt();        final int[] array = new int[sizeArray];        for(int i = 0; i < sizeArray; i++){            array[i] = scanner.nextInt();        }        scanner.close();        final int[] counters = findDigits(array, sizeArray);        for(final int count : counters){            System.out.println(count);        }    }    public static int[] findDigits(int[] array, int sizeArray){        final int[] counters = new int[sizeArray];        for (int i = 0; i < sizeArray; i++){            counters[i] = countDivisibleNumber(array[i]);        }        return counters;    }    public static int countDivisibleNumber(int number){        final int element = number;        int count = 0;        while(number != 0){ /*Number -> decimal digit*/            int digit = number % 10;            if(digit != 0){ /*Denominator zero not valid for division and modulo operators*/                if(element % digit == 0){                    count++;                }            }            number /= 10;        }        return count;    }}