package HackerRank;import java.util.Scanner;public class Java2DArray {    static final int size = 6;    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int[][] array = new int[size][size];        for(int row = 0; row < size; row++){            for(int column = 0; column < size; column++){                array[row][column] = scanner.nextInt();            }        }        System.out.println(sumMaxHourGlass(array, size));        scanner.close();    }    public static int sumMaxHourGlass(int[][] array, int size){        int result = Integer.MIN_VALUE;        size -= 2;        for(int row = 1; row <= size; row++){            for(int column  = 1; column <= size; column++){                result = Math.max(result, array[row - 1][column - 1] + array[row - 1][column] + array[row-1][column+1]                +array[row][column] + array[row + 1][column-1] + array[row + 1][column] + array[row + 1][column + 1]);            }        }        return result;    }}