package HackerRank;import java.util.Scanner;abstract class Book{    String title;    abstract void setTitle(String title);    String getTitle(){        return title;    }}class MyBook extends Book{    @Override    void setTitle(String title) {        this.title = title;    }}public class JavaAbstractClass {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final String title = scanner.nextLine();        MyBook novelBook = new MyBook();        novelBook.setTitle(title);        System.out.println("The title is: "+novelBook.getTitle());        scanner.close();    }}