package HackerRank;import java.util.Scanner;public class JavaArrayD1 {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int size = scanner.nextInt();        final int[] array = new int[size];        for(int i = 0; i < size; i++){            array[i] = scanner.nextInt();        }        scanner.close();        showArrayElements(array);    }    public static void showArrayElements(int[] array){        for(final int x: array){            System.out.println(x);        }    }}