package HackerRank;import java.util.ArrayList;import java.util.List;import java.util.Scanner;/*** * */public class JavaArraylist {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        /*Input part START*/        List <List<Integer>> listOfList1 = new ArrayList<>(); /*2D Array List*/        int n = scanner.nextInt(); /*Input line number*/        for(int i = 0; i < n; i++){            int d = scanner.nextInt(); /*Number of integers*/            List <Integer> list = new ArrayList<>();            for(int j = 0; j < d; j++){                list.add(scanner.nextInt());            }            listOfList1.add(list);        }        /*Input part END*/        /*Output part START*/        int q = scanner.nextInt(); /*Input query number*/        for(int i = 0; i < q; i++){            int x = scanner.nextInt(); /*Denoted line's position*/            int y = scanner.nextInt(); /*Denoted x^th line of element of position*/            try {                System.out.println(listOfList1.get(x - 1).get(y-1));            }            catch (Exception e){                System.out.println("ERROR!");            }        }        /*OutPut part END*/        scanner.close();    }}