package HackerRank;import java.math.BigDecimal;import java.util.Arrays;import java.util.Comparator;import java.util.Scanner;/*** * Application of custom comparator with BigDecimal class */public class JavaBigDecimalHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int SIZE = scanner.nextInt();        final String[] numbers = new String[SIZE + 2];        for(int i = 0; i < SIZE; i++){            numbers[i] = scanner.next();        }        scanner.close();        /*Custom comparator*/        Comparator <String> comparator = new Comparator<String>() {            @Override            public int compare(String o1, String o2) {                BigDecimal bigDecimal1 = new BigDecimal(o1);                BigDecimal bigDecimal2 = new BigDecimal(o2);                return bigDecimal2.compareTo(bigDecimal1); /*Descending ordered*/            }        };        Arrays.sort(numbers, 0, SIZE, comparator);        for(int i = 0; i < SIZE; i++){            System.out.println(numbers[i]);        }    }}