package HackerRank;import java.math.BigInteger;import java.util.Scanner;public class JavaBigIntegerHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final BigInteger number1 = scanner.nextBigInteger();        final BigInteger number2 = scanner.nextBigInteger();        scanner.close();        System.out.println(addThem(number1, number2));        System.out.println(multiplyThem(number1, number2));    }    public static BigInteger addThem(BigInteger number1, BigInteger number2){        return number1.add(number2);    }    public static BigInteger multiplyThem(BigInteger number1, BigInteger number2){        return number1.multiply(number2);    }}