package HackerRank;import java.util.Scanner;public class JavaDataTypesHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int T = scanner.nextInt();        for(int i = 0; i < T; i++){            String x = scanner.next();            try {                primitiveDataTypesRanged(Long.parseLong(x));            }            catch (NumberFormatException numberFormatException){                System.out.println(x+" can't be fitted anywhere.");            }        }        scanner.close();    }    public static void primitiveDataTypesRanged(long number){        System.out.println(number+" can be fitted in:");        if(number >= -Math.pow(2, 7) && number < Math.pow(2, 7)){            System.out.println("* byte");        }        if(number >= -Math.pow(2, 15) && number < Math.pow(2, 15)){            System.out.println("* short");        }        if(number >= -Math.pow(2, 31) && number < Math.pow(2, 31)){            System.out.println("* int");        }        if(number >= -Math.pow(2, 63) && number < Math.pow(2, 63)){            System.out.println("* long");        }    }}