package HackerRank;import java.text.DateFormatSymbols;import java.util.Calendar;import java.util.Scanner;public class JavaDateAndTime {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int month = scanner.nextInt();        int day = scanner.nextInt();        int year = scanner.nextInt();        Calendar calendar = Calendar.getInstance();        calendar.set(year, month-1, day);        System.out.println(new DateFormatSymbols().getWeekdays()[calendar.get(Calendar.DAY_OF_WEEK)].toUpperCase());        scanner.close();    }}