package HackerRank;import java.util.Scanner;/*** * Throws and throw keywords */class MyCalculatorPow {    public long power(int n, int p) throws Exception {        if(n == 0 && p == 0){            throw new Exception("n and p should not be zero.");        }        else if(n < 0 || p < 0){            throw new Exception("n or p should not be negative.");        }        return (long) Math.pow(n, p);    }}public class JavaExceptionHandling {    public static final MyCalculatorPow my_calculator = new MyCalculatorPow();    public static final Scanner in = new Scanner(System.in);    public static void main(String[] args) {        while(in.hasNext()){            int n = in.nextInt();            int p = in.nextInt();            try {                System.out.println(my_calculator.power(n, p));            }            catch (Exception exception){                System.out.println(exception.getMessage());            }        }    }}