package HackerRank;import java.lang.reflect.Method;class Printer{    public <E> void printArray(E[] array){        for(final var x: array){            System.out.println(x);        }    }}public class JavaGenericHR {    public static void main(String[] args) {        Printer printer = new Printer();        final int[] intArray = {1, 2, 3};        final String[] stringArray = {"Hello, World"};        printer.printArray(intArray);        printer.printArray(stringArray);        int count = 0;        for (final Method method : Printer.class.getDeclaredMethods()){            String name = method.getName();            if(name.equals("printArray")){                count++;            }        }        if(count > 1){            System.out.println("Method overloading is not allowed");        }        assert count = 1;    }}