package HackerRank;import java.util.HashSet;import java.util.Scanner;import java.util.Set;/*** * Set: Unique element loader */public class JavaHashSet {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int TEST_CASE = scanner.nextInt();        final String[] pairLeft = new String[TEST_CASE];        final String[] pairRight = new String[TEST_CASE];        for(int i = 0; i < TEST_CASE; i++){            pairLeft[i] = scanner.next();            pairRight[i] = scanner.next();        }        Set <String> set = new HashSet<>();        for(int i = 0; i < TEST_CASE; i++){            set.add(pairLeft[i]+pairRight[i]);            System.out.println(set.size());        }        scanner.close();    }}