package HackerRank;class Arithmetic{    private int number1;    private int number2;    public Arithmetic(){        number1 = 0;        number2 = 0;    }    public Arithmetic(int number1){        this();        this.number1 = number1;    }    public Arithmetic(int number1, int number2){        this(number1);        this.number2 = number2;    }    public void setNumber1(int number1) {        this.number1 = number1;    }    public void setNumber2(int number2) {        this.number2 = number2;    }    public int getNumber1() {        return number1;    }    public int getNumber2() {        return number2;    }    public int add(int number1, int number2){        return (number1 + number2);    }}class Adder extends Arithmetic{}public class JavaInheritanceIIHR {    public static void main(String[] args) {        Adder adder = new Adder();        System.out.println("My super class: "+adder.getClass().getSuperclass().getName());        System.out.println(adder.add(10, 32)+" "+adder.add(10, 3)+" "+adder.add(10, 10));    }}