package HackerRank;import java.util.ArrayList;import java.util.Scanner;class Student1{}class Rockstar{}class Hacker{}public class JavaInstanceOfKeyWord{    static String count(ArrayList mylist){        int a = 0,b = 0,c = 0;        for (Object element : mylist) {            if (element instanceof Student1)                a++;            if (element instanceof Rockstar)                b++;            if (element instanceof Hacker)                c++;        }        final String result = Integer.toString(a)+" "+ Integer.toString(b)+" "+ Integer.toString(c);        return result;    }    public static void main(String []args){       final ArrayList mylist = new ArrayList();        Scanner scanner = new Scanner(System.in);        final int t = scanner.nextInt();        for(int i = 0; i < t; i++){            final String s = scanner.next();            if(s.equals("Student"))mylist.add(new Student1());            if(s.equals("Rockstar"))mylist.add(new Rockstar());            if(s.equals("Hacker"))mylist.add(new Hacker());        }        System.out.println(count(mylist));        scanner.close();    }}