package HackerRank;import java.util.Scanner;public class JavaIntToString {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int number = scanner.nextInt();        scanner.close();        if(!Integer.toString(number).isEmpty()){            System.out.println("Good job");        }        else {            System.out.println("Wrong answer");        }    }}