package HackerRank;import java.util.Scanner;interface AdvancedArithmetic{    int divisorSum(int n);}class MyCalculator implements AdvancedArithmetic{    @Override    public int divisorSum(int n) {        int sum = 0;        for(int i = 1; i <= n; i++){            if(n % i == 0){                sum += i;            }        }        return sum;    }}public class JavaInterfaceHR {    public static void main(String[] args) {        MyCalculator myCalculator = new MyCalculator();        System.out.println("I implemented: ");        implementedInterfaceNames(myCalculator);        Scanner scanner = new Scanner(System.in);        int n = scanner.nextInt();        System.out.println(myCalculator.divisorSum(n));        scanner.close();    }    static void implementedInterfaceNames(Object o){        final Class[] theInterface = o.getClass().getInterfaces();        for (final Class aClass : theInterface) {            String interfaceName = aClass.getName();            System.out.println(interfaceName);        }    }}