package HackerRank;import java.util.ArrayList;import java.util.List;import java.util.Scanner;public class JavaListHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int INITIAL_SIZE = scanner.nextInt(); /*Initial size of capacity*/        List<Integer> integerList = new ArrayList<>(INITIAL_SIZE);        for (int i = 0; i < INITIAL_SIZE; i++) {            integerList.add(scanner.nextInt()); /*Auto boxing, Primitive to owen wrapper class*/        }        final int QUERY = scanner.nextInt();        String operation = "";        for(int  i = 0; i < QUERY; i++){            operation = scanner.next();            if(operation.equalsIgnoreCase("Insert")){                int index = scanner.nextInt();                int element = scanner.nextInt();                integerList.add(index, element);            }            else if(operation.equalsIgnoreCase("Delete")){                int index = scanner.nextInt();                integerList.remove(index);            }        }        displayList(integerList);        scanner.close();    }    public static void displayList(List <Integer> integerList){        for(final Integer x: integerList){            System.out.print(x+" ");        }        System.out.println();    }}