package HackerRank;import java.util.Scanner;public class JavaLoopsII {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int q = scanner.nextInt();        int a = 0;        int b = 0;        int n = 0;        for(int i = 0; i < q; i++){            a = scanner.nextInt();            b = scanner.nextInt();            n = scanner.nextInt();            showSeries(a, b, n);        }        scanner.close();    }    public static void showSeries(int a, int b, int n){        int sum = a + b;        System.out.print(sum+" ");        for(int i = 1; i < n; i++){            sum += (int)(Math.pow(2,i)*b);            System.out.print(sum+" ");        }        System.out.println();    }}