package HackerRank;import java.util.HashMap;import java.util.Map;import java.util.Scanner;public class JavaMap {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int TEST_CASE = scanner.nextInt();        Map <String, Integer> map = new HashMap<>();        scanner.nextLine();        for (int i = 0; i < TEST_CASE; i++) {            String name = scanner.nextLine();            int phone = scanner.nextInt();            map.put(name, phone);            scanner.nextLine();        }        /*inquiry from dictionary || map*/        while(scanner.hasNext()){            String key = scanner.nextLine();            if(map.containsKey(key)){                System.out.println(key+"="+map.get(key));            }            else{                System.out.println("Not found");            }        }        scanner.close();    }}