package HackerRank;import java.math.BigInteger;import java.util.Scanner;/*** * Prime -> BigInteger class with isProbablePrime() method */public class JavaPrimalityTest {    final static Scanner scanner = new Scanner(System.in);    public static void main(String[] args) {        BigInteger number = scanner.nextBigInteger();        System.out.println(number.isProbablePrime(100)?"prime":"not prime");        scanner.close();    }}