package HackerRank;import java.util.Scanner;/** * Java custom regular expression */public class JavaRegex {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        while (scanner.hasNext()){            String ip = scanner.next();            System.out.println(ip.matches(new MyRegex().pattern));        }        scanner.close();    }}class MyRegex{    /**     * [01]?\\d{1,2} match numbers 0-199     * 2[0-4]\\d match numbers 200-249     * 25[0-5] match numbers 250-255     */    String num = "([01]?\\d{1,2}|2[0-4]\\d|25[0-5])";    String pattern = num+"."+num+"."+num+"."+num; /*IPv4*/}