package HackerRank;import java.util.Arrays;import java.util.Scanner;class Student{    int id;    String name;    double cgpa;    Student(){        id = 0;        name = "";        cgpa = 0.0;    }    Student(int id){        this();        this.id = id;    }    Student(int id, String name){        this(id);        this.name = name;    }    Student(int id, String name, double cgpa){        this(id, name);        this.cgpa = cgpa;    }}public class JavaSort {    final static double ERROR = 1E-9;    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int size = scanner.nextInt();        final Student[] students = new Student[size];        for(int i  = 0; i < size; i++){            int id = scanner.nextInt();            String name = scanner.next();            double cgpa = scanner.nextDouble();            students[i] = new Student(id, name, cgpa);        }        /*Math.sigma(number) -> sign of the number*/        Arrays.sort(students,                (student1, student2)-> Math.abs(student1.cgpa - student2.cgpa) > ERROR                ?(int)Math.signum(student2.cgpa - student1.cgpa):                (student1.name.equals(student2.name) ? (student1.id - student2.id):                        student1.name.compareTo(student2.name)));        for(final Student student : students){            System.out.println(student.name);        }        scanner.close();    }}