package HackerRank;import java.util.Scanner;import java.util.Stack;public class JavaStack {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        while(scanner.hasNext()){            String input = scanner.next();            System.out.println(isValid(input));        }        scanner.close();    }    public static boolean isValid(String input) {        final int SIZE = input.length();        Stack<Character> characterStack = new Stack<>();        for (int i = 0; i < SIZE; i++) {            char ch = input.charAt(i);            if (ch == '(' || ch == '{' || ch == '[') {                characterStack.push(ch);            }            else if(characterStack.isEmpty()){                return false;            }            else {                char top = characterStack.pop();                if ((top == '(' && ch != ')') ||                        (top == '{' && ch != '}') ||                        (top == '[' && ch != ']')) {                    return false;                }            }        }        return characterStack.isEmpty();    }}