package HackerRank;import java.util.Scanner;public class JavaStaticInitializerBlock {    public static int breadth;    public static int height;    static Scanner scanner = new Scanner(System.in);    static {        breadth = scanner.nextInt();        height = scanner.nextInt();    }    public static void main(String[] args) {        try {            if(breadth <= 0 || height <= 0){                throw new Exception("Breadth and height must be positive");            }            System.out.println(breadth * height);        }        catch (Exception exception){            System.out.println("java.lang.Exception: "+exception.getMessage());        }    }}