package HackerRank;import java.util.Scanner;public class JavaStringIntroductionHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String word1 = scanner.next();        String word2 = scanner.next();        scanner.close();        System.out.println(word1.length() + word2.length());        if(word1.compareTo(word2) > 0){            System.out.println("Yes");        }        else{            System.out.println("No");        }        String words = toUpperChar(word1.charAt(0)) + word1.substring(1) + " ";        words += toUpperChar(word2.charAt(0)) + word2.substring(1);        System.out.println(words);    }    public static char toUpperChar(char ch){        return (char) (ch - 32);    }}