package HackerRank;import java.util.Scanner;public class JavaStringReverseHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final String word = scanner.next();        scanner.close();        System.out.println(isPalindrome(word)?"Yes":"No");    }    public static boolean isPalindrome(String word){        final int size = word.length();        int startIndex = 0;        int endIndex = size - 1;        while(startIndex < endIndex){            if(word.charAt(startIndex) != word.charAt(endIndex)){                return false;            }            startIndex++;            endIndex--;        }        return true;    }}