package HackerRank;import java.util.Arrays;import java.util.Scanner;public class JavaStringTokens {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        String line = scanner.nextLine();        String[] words = line.split("[^A-Za-z]+");        if(words.length > 0 && words[0].isEmpty()){ /*First index empty*/            words = Arrays.copyOfRange(words, 1, words.length);        }        System.out.println(words.length);        for(final String word : words){            System.out.println(word);        }        scanner.close();    }}