package HackerRank;import java.util.Scanner;/*** * Generated contiguous sub-array list from an array */public class JavaSubArrayHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int SIZE = scanner.nextInt();        final int[] array = new int[SIZE];        for(int i = 0; i < SIZE; i++){            array[i] = scanner.nextInt();        }        int count = 0;        /*Contiguous sub-array list*/        for(int i = 0; i < SIZE; i++){            int sum = 0;            for(int j = i; j < SIZE; j++){                sum += array[j];                if(sum < 0){                    count++;                }            }        }        System.out.println(count);        scanner.close();    }}