package HackerRank;import java.util.Scanner;public class LeftRotationHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        int arraySize = scanner.nextInt();        final int[] array = new int[arraySize];        int shift = scanner.nextInt();        for(int i = 0; i < arraySize; i++){            array[i] = scanner.nextInt();        }        for(int i = 0; i < shift; i++){ /*Shifting times*/            leftRotation(array, arraySize);        }        showArray(array);        scanner.close();    }    public static void leftRotation(int[] array, int arraySize){ /*One times invoked single left shift*/        int temp = array[0];        for(int i = 1; i < arraySize; i++){            array[i-1] = array[i];        }        array[arraySize - 1] = temp;    }    public static void showArray(int[] array){        for(final int x: array){            System.out.print(x+" ");        }    }}