package HackerRank;import java.util.Scanner;/*** * Best practice using physic law */public class NumberLineJumps {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.println(kangaroo(scanner.nextInt(),scanner.nextInt(), scanner.nextInt(), scanner.nextInt()));        scanner.close();    }    public static String kangaroo(int x1, int v1, int x2, int v2){        if(v1 <= v2){            return "NO";        }        else if((x2 - x1) % Math.abs(v1 - v2) == 0){            return "YES";        }        else{            return "NO";        }    }}