package HackerRank;import java.util.Scanner;/*** * Most important for viva */public class NumberLineJumpsLoop {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        System.out.println(kangaroo(scanner.nextInt(),scanner.nextInt(), scanner.nextInt(), scanner.nextInt()));        scanner.close();    }    public static String kangaroo(int x1, int v1, int x2, int v2){        if(v1 <= v2){            return "NO";        }        x1 = Math.min(x1, x2); /*Make sure x1 is smaller than x2*/        while (x1 < x2){            x1 += v1;            x2 += v2;        }        if(x1 == x2){            return "YES";        }        else{            return "NO";        }    }}