package HackerRank;import java.util.Scanner;public class SimpleArraySumHR {    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int sizeArray = scanner.nextInt();        final int[] array = new int[sizeArray];        for(int i = 0; i < sizeArray; i++){            array[i] = scanner.nextInt();        }        scanner.close();        System.out.println(simpleArraySum(array));    }    public static int simpleArraySum(int[] array){        int sum = 0;        for(final int x: array){            sum += x;        }        return sum;    }}