package HackerRank;import java.util.Scanner;import java.util.regex.Matcher;import java.util.regex.Pattern;/*** * Tag matcher of tag based language (HTML, XML) */public class TagContentExtractor {    private final static Scanner scanner = new Scanner(System.in);    public static void main(String[] args) {        int testCase = Integer.parseInt(scanner.nextLine());        while(testCase-- > 0){            String line = scanner.nextLine();            Pattern pattern = Pattern.compile("<(.+)>([^<]+)</\\1>");            Matcher matcher = pattern.matcher(line);            boolean found = false;            while(matcher.find()){                System.out.println(matcher.group(2));                found = true;            }            if(!found){                System.out.println("None");            }        }        scanner.close();    }}