package HackerRank;import java.util.Scanner;public class TwoDArrayDS {    final static int size = 6;    public static void main(String[] args) {        Scanner scanner = new Scanner(System.in);        final int [][] array = new int[size][size];        for(int row = 0; row < size ; row++){            for(int column = 0; column < size; column++){                array[row][column] = scanner.nextInt();            }        }        System.out.println(maxSumHourGlass(array, size));        scanner.close();    }    public static int maxSumHourGlass(int[][] array, int size){        int maxHourGlass = Integer.MIN_VALUE;        int sumHourGlass = 0;        size -= 2;        for(int row = 0; row < size; row++){            for(int column = 0; column < size; column++){                sumHourGlass += array[row][column] + array[row][column + 1] + array[row][column + 2];                sumHourGlass += array[row + 1][column + 1];                sumHourGlass +=array[row + 2][column] + array[row + 2][column + 1] + array[row + 2][column + 2];                maxHourGlass = Math.max(maxHourGlass, sumHourGlass);                sumHourGlass = 0;            }        }        return maxHourGlass;    }}