package HackerRank;public class ValidUserNameRegularEx {    public static void main(String[] args) {        String name  = "yousuf";        System.out.println(name.matches("[a-z]"));    }    public static boolean isValidUserName(String userNames){        final int size = userNames.length();        if(size < 8 || size >= 30){            return false;        }        else if(userNames.charAt(0) == '_'){            return false;        }        else if(userNames.matches("[a-z]")){            return true;        }        for(int i = 1; i < size; i++){            if(userNames.charAt(i) == '_'){                return true;            }        }        return true;    }}