package HackerRank;/*** * Java substring comparing dictionary order */public class javaSubStringComparisons {    public static void main(String[] args) {        String s = "welcometojava";        int k = 3;        System.out.println(getSmallestAndLargest(s, k));    }    public static String getSmallestAndLargest(String s, int k){        final int LENGTH = s.length();        /*Firstly pick first substring*/        String currentSubString = s.substring(0, k); /*k = 3*/        String smallest = currentSubString;        String largest = currentSubString;        /*Starting second substring*/        for(int i = 1; i <= LENGTH - k; i++){           currentSubString = s.substring(i, i+k); /*at first index 1 to next 3 index*/           if(currentSubString.compareTo(smallest) < 0){               smallest = currentSubString;           }           if(currentSubString.compareTo(largest) > 0){               largest = currentSubString;           }        }        return smallest+"\n"+largest;    }}